#!/usr/bin/env ruby

test1 = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'
test2 = 'bvwbjplbgvbhsrlpgdmjqwftvncz'
test3 = 'nppdvjthqldpwncqszvftbrmjlhg'
test4 = 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg'
input = ''

def is_marker(str, index)
	sub_str = str[index - 14, 14]
	return sub_str.chars.uniq.length == 14
end

def find_start(str)
	i = 14
	until is_marker(str, i)
		i += 1
	end
	puts i
end

find_start(test1)
find_start(test2)
find_start(test3)
find_start(test4)
find_start(input)
